pair A, B, C, D, E, F, G, P, Q, R;

A = (0,3);
B = (0,0);
C = (4,0);
D = (B + C)/2;
E = (C + A)/2;
F = (A + B)/2;
G = (A + B + C)/3;
P = (G + reflect(B,C)*(G))/2;
Q = (G + reflect(C,A)*(G))/2;
R = (G + reflect(A,B)*(G))/2;

draw(A--B--C--cycle);
draw(A--G);
draw(B--G);
draw(C--G);
draw(G--P);
draw(G--Q);
draw(G--R);

